var el1=document.getElementById("prename");var el2=document.getElementById("name");var el3=document.getElementById("reflink");var el4=document.getElementById("e-mail");var el5=document.getElementById("agv");var el6=document.getElementById("sponsor");function input(a){if(a.value.length>0){setClass(a,"is-invalid","is-valid")}else{setClass(a,"is-valid","is-invalid")}}function inputbtn(a){if(a.checked){setClass(a,"is-invalid","is-valid")}else{setClass(a,"is-valid","is-invalid")}}function setClass(e,a,f){var g=e.className.split(" ");for(var d=0;d<g.length;d++){if(g[d]==a){g[d]=f}}var c="";for(var b=0;b<g.length;b++){c+=g[b]+" "}e.className=c}function setTravelbtn(a){var b=document.getElementById("sendMessageButton");if(a){b.removeAttribute("disabled")}else{var c=document.createAttribute("disabled");b.setAttributeNode(c)}}function count(){return getAllTagsWithValueAttribute("class","is-invalid").length}setInterval(function(){input(el1);input(el2);input(el3);input(el4);inputbtn(el5);input(el6);if(count()<=0){setTravelbtn(true)}else{setTravelbtn(false)}},100);